// App.js
import React, { useState } from 'react';
import MovieList from './MovieList';
import Filter from './Filter';

import './App.css'; // Import your CSS file for styling

const App = () => {
  const [movies, setMovies] = useState([
    {
      title: 'Inception',
      description: 'A mind-bending thriller.',
      posterURL: 'data:image/jpeg;base64,',
      rating: 4.5,
    },
    {
      title: 'Nowhere',
      description: 'Fleeing a totalitarian country',
      posterURL: 'data:image/jpeg;base64,',
      rating: 3.5,
    },
    {
      title: 'Shawnty town',
      description: 'A group of courtesans',
      posterURL: 'data:image/jpeg;base64,',
      rating: 5.0,
    },
    {
      title: 'Barbie',
      description: 'To live in Barbie Land',
      posterURL: 'data:image/jpeg;base64,',
      rating: 3.8,
    },
    {
      title: 'Creed',
      description: 'After dominating the boxing world',
      posterURL: 'data:image/jpeg;base64,',
      rating: 5.0,
    }
    
  ]);

  const [filter, setFilter] = useState({
    title: '',
    rating: '',
  });

  const handleFilterChange = (key, value) => {
    setFilter({ ...filter, [key]: value });
  };

  const filteredMovies = movies.filter(
    (movie) =>
      movie.title.toLowerCase().includes(filter.title.toLowerCase()) &&
      (filter.rating === '' || movie.rating >= parseFloat(filter.rating))
  );

  return (
    <div className="app">
      <h1>Movie App</h1>
      <Filter onFilterChange={handleFilterChange} />
      <MovieList movies={filteredMovies} />
      {/* Add functionality to add new movies here */}
    </div>
  );
};

export default App;
